import ella_site_desktop from './images/site_ella_desktop.png';
import site_nave_desktop from './images/site_nave_do_conhecimento_desktop.png';
import icaros_mobile from './images/icaros_mobile.png';


var projetos = [
    {
        'titulo':'Site minha namorada', 
        'resumo':'Um site que eu fiz para minha namorada no começo dos meus estudos,por esse fato, diversas coisas estão em seus modelos iniciais,mas em algum momento eu pretendo recriá-lo com meus novos conhecimentos.',
        'link_site': 'https://ella-site.vercel.app',
        'link_github':'https://github.com/subaru3rem/ella-site',
        'imagem':ella_site_desktop
    },
    {
        'titulo':'Nave do Conhecimento',
        'resumo':'Esse é um projeto full-stack que eu fiz ao fim do meu curso de front-end e continuei ao adquirir conhecimentos de servidor estudando flask em python, é uma reconstrução completa do site com um mini sistema de altenticação e gerenciamento de cookies',
        'link_github':'https://github.com/subaru3rem/Nave_do_conhecimento',
        'imagem': site_nave_desktop
    },
    {
        'titulo':'Icaros',
        'resumo':'Um projeto mobile que eu fiz para me saciar com algumas finalidades que eu considero util de se ter, além de me dar um conhecimento que eu sempre quis no desenvolvimento mobile',
        'link_github':'https://github.com/subaru3rem/Icaros',
        'imagem': icaros_mobile
    }
]
function Projeto(projeto){
    return (
          <div className='projeto'>
            <div>
            <h1>{projeto.titulo}</h1>
            <p>{projeto.resumo}</p>
            <p>
              {verif(projeto)}
              link github:<br/> <a href={projeto.link_github}>{projeto.link_github}</a>
            </p>
            </div> 
            <img src={projeto.imagem}/>
          </div>
    );
};
export function Projetos(){
    var projetos_div = projetos.map(projeto_obj => Projeto(projeto_obj));
    return projetos_div;
}
function verif(props){
    if(props.link_site){
        return <p>link do site:<br/><a href={props.link_site}>{props.link_site}</a><br/><br/></p>
    }
    else{
        return ''
    }
}
export class Buttons{
    constructor(){
      this.indice = 0;
    };
    button_next(){
      var projetos = document.getElementsByClassName('projeto');
      projetos[this.indice].style.display = 'none';
      projetos[this.indice+1].style.display = 'flex';
      if(this.indice != projetos.length){this.indice+=1}else{this.indice=0}
    };
};



export default Projetos;
